model.metadata:
CREATE FOREIGN TABLE customers (
	_id object,
	customer_id integer,
	firstName string,
	lastName string,
	CONSTRAINT PK0 PRIMARY KEY(_id)
) OPTIONS(UPDATABLE 'TRUE');

CREATE FOREIGN TABLE key (
	_id integer,
	CONSTRAINT PK0 PRIMARY KEY(_id)
) OPTIONS(UPDATABLE 'TRUE', "teiid_mongo:MERGE" 'system.indexes');

CREATE FOREIGN TABLE system.indexes (
	v integer,
	name string,
	ns string
) OPTIONS(UPDATABLE 'TRUE');
